import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
const alarm = "https://cfa-sounds.s3-us-west-1.amazonaws.com/Mission+Meow.m4a";

export function TimerDisplay(props) {
  let workTimeLeft;
  props.workTime === 0 ? workTimeLeft = new Date(0).toISOString().substring(14,19) : workTimeLeft = moment.duration(props.workTime, "seconds").format("mm:ss");
  let breakTimeLeft;
  props.breakTime === 0 ? breakTimeLeft = new Date(0).toISOString().substring(14,19) : breakTimeLeft = moment.duration(props.breakTime, "seconds").format("mm:ss");

  return (
    <Card>
      {props.working && 
      <Button id="start_stop" variant="primary" onClick={props.handleWorkClick} >{props.workTimerOn  ? "Pause Timer" : "Start Timer"}</Button>
      }
      {!props.working && 
      <Button id="start_stop" variant="primary" onClick={props.handleBreakClick} >{props.breakTimerOn  ? "Pause Timer" : "Start Timer"}</Button>
      }
      <div id="timer-label" >Session:
      {props.workTimerOn && "Work Session"}
      {props.breakTimerOn && "Break Session"}
      </div>
      <h1 id="time-left">{props.working ? workTimeLeft : breakTimeLeft}</h1>

        <audio
          id="beep"
          src={alarm} >
            Your browser does not support the
            <code>audio</code> element.
         </audio>
      <Button id="reset" variant="primary" onClick={props.reset} >Reset</Button>
      </Card>
  );
}
